import { CSSProperties, ReactElement, ReactNode } from 'react';
import { CascaderOption } from '../cascader';
import { InputProps } from '../input';
import { BaseProps } from '../interface';
export interface CascaderPaneProps extends BaseProps {
    /**
     * 当前展开的key
     */
    value?: Array<string | number>;
    /**
     * 默认选中的key
     */
    defaultValue?: Array<string | number>;
    /**
     * options
     */
    options?: CascaderOption[];
    /**
     * 展开的trigger
     */
    expandTrigger?: 'click' | 'hover';
    /**
     * 点击item触发的回调
     */
    onSelect?: (targetOption: CascaderOption, menuIndex: number, activeValue: Array<string | number>) => void;
    /**
     * hover item触发的回调
     */
    onHover?: (targetOption: CascaderOption, menuIndex: number, activeValue: Array<string | number>) => void;
    /**
     * 面板是否可见，常用做下拉面板里面
     */
    visible?: boolean;
    /**
     * 级联面板自定义样式
     */
    cascaderPaneStyle?: CSSProperties;
    /**
     * 指定的fieldName的对象
     */
    fieldNames?: {
        label?: string;
        value?: string;
        children?: string;
        icon?: string;
    };
    /**
     * 展开的icon
     */
    expandIcon?: ReactNode;
    /**
     * 正在加载的icon
     */
    loadingIcon?: ReactNode;
    /**
     * 多选选中的keys
     */
    checkedKeys?: Array<string | number>;
    /**
     * 默认多选选中的keys
     */
    defaultCheckedKeys?: Array<string | number>;
    /**
     * 是否多选
     */
    showCheckbox?: boolean;
    /**
     * 展示全部
     */
    showCheckAll?: boolean;
    /**
     * 空数据展示类型
     */
    emptyType?: 'option' | 'card';
    /**
     * @deprecated
     */
    checkboxPrefixCls?: string;
    /**
     * 多选变化回调
     */
    onCheckboxChange?: (checkedKeys: Array<string | number>) => void;
    /**
     * 是否展示搜索
     */
    showSearch?: boolean;
    /**
     * 自定义搜索的props
     */
    searchProps?: InputProps;
    /**
     * 面板宽度
     */
    paneWidth?: number | string;
    /**
     * 菜单列宽度，最后一列自动撑满
     */
    menuWidth?: number | string;
    /**
     * 尺寸
     */
    size?: 'small' | 'medium' | 'large';
    /**
     * 是否使用默认筛选
     */
    useDefaultFilter?: boolean;
    /**
     * input输入时候的onChange
     */
    onInputChange?: InputProps['onChange'];
    /**
     * 默认搜索的value - 非受控
     */
    defaultSearchValue?: string;
    /**
     * 搜索的value - 受控
     */
    searchValue?: string;
    /**
     * 空节点node
     */
    emptyNode?: ReactNode;
    /**
     * 点击搜索item的回调
     */
    onClickSearchItem?: (option: CascaderOption[]) => void;
    /**
     * isFirstColumnLikeTree为树结构时候，该结构挂的group
     */
    firstColumnGroup?: Array<{
        label: string;
        value: string;
        children: string[];
    }> | boolean;
    /**
     * 自定义render
     */
    CustomItemRender?: ReactNode;
    /**
     * 输入框类型
     */
    inputType?: 'inline' | 'normal';
    /**
     * 用于动态加载选项
     */
    loadData?: (params: {
        parent: CascaderOption;
        value: Array<string | number>;
        scope: 'descendants' | 'children';
        trigger: 'expand' | 'check';
    }) => Promise<void>;
    /**
     * 自定义选项
     */
    renderOption?: (param: {
        node: ReactElement;
        option: CascaderOption;
    }) => ReactElement;
}
