import { ReactNode, FocusEventHandler } from 'react';
import { BaseProps } from '../interface';
export interface NumberInputProps extends BaseProps {
    /**
     * 默认值
     */
    defaultValue?: string | number;
    /**
     * 当前值，受控
     */
    value?: string | number;
    /**
     * value类型为number
     */
    valueAsNumber?: boolean;
    /**
     * 输入数字的类型，float或int
     */
    type?: 'float' | 'int';
    /**
     * 数字输入框中无内容时显示的提示文字
     */
    placeholder?: string;
    /**
     * 禁用状态
     */
    disabled?: boolean;
    /**
     * 最大值
     */
    max?: number;
    /**
     * 最小值
     */
    min?: number;
    /**
     * 调节按钮点击时值跳动的步频
     */
    step?: number;
    /**
     * 保留的小数位数，只有当type为float时有效
     */
    fixed?: number;
    /**
     * 值变化回调
     */
    onChange?: (e: {
        target: {
            value: number | string;
        };
    }) => void;
    /**
     * 聚焦回调
     */
    onFocus?: FocusEventHandler<HTMLInputElement>;
    /**
     * 失焦回调
     */
    onBlur?: FocusEventHandler<HTMLInputElement>;
    /**
     * 是否显示默认提示
     */
    showTip?: boolean;
    /**
     * 默认提示话术
     */
    tipText?: ReactNode;
    /**
     * 报错信息
     */
    errorMessage?: ReactNode;
    /**
     * 提示位置和报错位置，默认统一
     * @deprecated
     */
    location?: 'right' | 'bottom' | 'layer';
    /**
     * 提示位置，取值为right、bottom、layer，如未设置，取自location
     * @deprecated
     */
    tipLocation?: 'right' | 'bottom' | 'layer';
    /**
     * 报错位置，取值为right、bottom、layer，如未设置，取自location
     * @deprecated
     */
    errorLocation?: 'right' | 'bottom' | 'layer';
    /**
     * 输入框后信息，一般为单位
     */
    tailLabel?: string;
    /**
     * 宽度
     */
    width?: number;
    /**
     * 类型; strong为加强型
     */
    mode?: 'basic' | 'strong';
    /**
     * 尺寸
     */
    size?: 'xsmall' | 'small' | 'medium';
    /**
     * 只读
     */
    readOnly?: boolean;
    /**
     * 报错，但是没有错误提示，常用于有两个numberbox并列的时候第一个numberbox报错
     */
    showErrorWithoutErrorMessage?: boolean;
    /**
     * showErrorMessage为false的时候可以业务可以自己定义errorMessage的位置
     */
    showErrorMessage?: boolean;
    /**
     * 自动focus
     */
    autoFocus?: boolean;
    /**
     * 是否展示数值调节按钮
     */
    showSpinButtons?: boolean;
    /**
     * 前缀
     */
    prefix?: ReactNode;
    /**
     * 后缀
     */
    suffix?: ReactNode;
}
