import { BaseProps } from '../interface';
export interface PaginationProps extends BaseProps {
    /**
     * 总行数
     */
    total?: number;
    /**
     * 当前页码
     */
    pageNo?: number;
    /**
     * 每页展示数
     */
    pageSize?: number;
    /**
     * 默认的pageNo
     */
    defaultPageNo?: number;
    /**
     * 默认的pageSize
     */
    defaultPageSize?: number;
    /**
     * 页数change的时候触发的函数， 传出e
     */
    onPageNoChange?(e: {
        target: {
            value: number;
        };
    }): void;
    /**
     * 是否隐藏分页器，如果只有一页的情况
     */
    hideOnSinglePage?: boolean;
    /**
     * 是否展示分页器，页码下拉选择
     */
    showSizeChange?: boolean;
    /**
     * 页码选项
     */
    pageSizeOptions?: number[];
    /**
     * 页码改变的时候触发的函数，传出e
     */
    onPageSizeChange?(e: {
        target: {
            value: number;
        };
    }): void;
    /**
     * 分页器尺寸 small; medium; large
     */
    size?: 'xsmall' | 'small' | 'medium';
    /**
     * 是否展示总计
     */
    showTotal?: boolean;
    /**
     * 页码下拉选择器类名前缀
     */
    selectPrefixCls?: string;
    /**
     * 是否展示跳页器
     */
    showPageJumper?: boolean;
    /**
     * pagination的select的props
     */
    selectProps?: object;
}
