import { BaseProps } from "../interface";
export interface ProgressProps extends BaseProps {
    /**
     * 类型，line 进度条 circle 进度环
     */
    type?: 'line' | 'circle';
    /**
     * 尺寸
     */
    size?: 'xsmall' | 'small' | 'medium';
    /**
     * 百分比
     */
    percent?: number;
    /**
     * 内容的模板函数
     */
    format?: (progress: number) => void;
    /**
     * 是否显示进度数值或状态图标
     */
    showInfo?: boolean;
    /**
     * 状态，
     * success 完成
     * exception 报错
     * normal 进行中
     */
    status?: 'normal' | 'success' | 'exception';
    /**
     * 进度条边缘的形状，
     * round 圆角
     * square 直角
     */
    strokeLinecap?: 'round' | 'square';
    /**
     * 进度条颜色
     */
    strokeColor?: string;
    /**
     * 进度条线的宽度，单位 px
     */
    strokeWidth?: number;
    /**
     * 画布宽度
     */
    width?: number | string;
    /**
     * 进度环背景色
     */
    trailColor?: string;
    /**
     * 进度条刷新操作
     */
    onRetry?: () => void;
    /**
     * 进度条取消操作
     */
    onCancel?: () => void;
}
