import { ReactNode } from "react";
import { BaseProps, BasePropsWithChildren } from "../interface";
export interface StepsProps extends BaseProps {
    /**
     * 当前步骤
     */
    current?: number;
    /**
     * 步骤条的方向
     */
    direction?: string;
    /**
     * 描述文案放置的位置
     */
    labelPlacement?: string;
    /**
     * 是否需要展示hover状态
     */
    showTipWhenHover?: boolean;
    /**
     * size 步骤条的尺寸
     */
    size?: 'small' | 'medium';
    /**
     * 当前步骤的状态
     */
    status?: 'wait' | 'process' | 'finish' | 'error';
    /**
     * 类型：点状 | 默认
     */
    type?: 'dot' | 'default';
    /**
     * initialStep 初始化的步骤条
     */
    initialStep?: number;
    /**
     * 点击step的回调函数，请用onChange代替
     * ⚠️问题：类型为string，从1开始与current不一致
     * @deprecated
     */
    onClickStep?: (step: string) => void;
    /**
     * 点击step的回调函数
     */
    onChange?: (step: number) => void;
}
export interface StepProps extends BasePropsWithChildren {
    /**
     * 标题
     */
    title?: ReactNode;
    /**
     * 描述
     */
    description?: ReactNode;
    /**
     * 可自定义当前步骤节点的宽度
     */
    itemWidth?: string | number;
    /**
     * 当前步骤的状态
     */
    status?: 'wait' | 'process' | 'finish' | 'error';
    /**
     * 是否需要在当前步骤条展示hover状态
     */
    showTipWhenHover?: boolean;
    /**
     * 自定义步骤条item的右边margin
     * @deprecated
     */
    adjustMarginRight?: string | number;
    /**
     * 步骤条连接线
     */
    tailContent?: ReactNode;
    /**
     * 自定义icon
     */
    icon?: ReactNode;
    /**
     * 状态icon
     */
    icons?: {
        finish?: ReactNode;
        error?: ReactNode;
    };
    /**
     * 自定义icon的className
     */
    iconClassName?: string;
    /**
     * 自定义hoverTip
     */
    hoverTip?: string;
}
