import { ReactNode, MouseEvent } from "react";
import { BasePropsWithChildren } from "../interface";
export interface TabsProps extends BasePropsWithChildren {
    /**
     * 尺寸
     */
    size?: 'small' | 'medium';
    /**
     * 类型
     */
    type?: 'line' | 'simple' | 'strong';
    /**
     * 是否带底边，type为line时生效
     */
    bordered?: boolean;
    /**
     * 当前被选中的key - 受控
     */
    activeKey?: string;
    /**
     * 默认当前被选中的key - 非受控
     */
    defaultActiveKey?: string;
    /**
     * 是否可以排序
     */
    sortable?: boolean;
    /**
     * 排序回调
     */
    onSort?: (fromIndex: number, toIndex: number) => void;
    /**
     * 是否展示添加按钮
     */
    showAdd?: boolean;
    /**
     * 添加按钮禁用
     */
    showAddDisabled?: boolean;
    /**
     * 线性按钮支持自定义添加按钮文案
     */
    addButtonText?: ReactNode;
    /**
     * 点击新增
     * @param activeKey 当前激活的key
     */
    onAdd?: (activeKey: string) => void;
    /**
     * 删除前回调，返回值为false，则不继续删除
     */
    onBeforeDelete?: (key: string) => Promise<boolean>;
    /**
     * 点击删除
     */
    onDelete?: (key: string) => void;
    /**
     * 点击tab的回调
     */
    onTabClick?: (key: string) => void;
    /**
     * 切换tab的时候的回调
     * @param activeKey 当前激活的key
     */
    onChange?: (activeKey: string) => void;
    /**
     * 点击往后翻页按钮的回调
     */
    onNextClick?: (e: MouseEvent) => void;
    /**
     * 点击往前翻页按钮的回调
     */
    onPrevClick?: (e: MouseEvent) => void;
    /**
     * 隐藏tabs的下边距
     */
    hideSpace?: boolean;
    /**
     * tabs右侧自定义区
     */
    extra?: ReactNode;
}
export interface TabPaneProps extends BasePropsWithChildren {
    /**
     * tab的key，必传
     */
    key?: string;
    /**
     * 状态
     */
    status?: 'success' | 'info' | 'error' | 'warning';
    /**
     * 选项卡头显示的文字
     */
    tab?: ReactNode;
    /**
     * 选项文字前可展示icon
     */
    Icon?: ReactNode;
    /**
     * 是否可以关闭
     */
    closable?: boolean;
    /**
     * 是否是禁用态
     */
    disabled?: boolean;
}
