import {
    ReactNode,
    MouseEvent
} from "react";

import {BasePropsWithChildren} from '../interface';

export interface AnchorLinkProps extends BasePropsWithChildren {
    /**
     * 锚点对应的#id
     */
    href?: string;
    /**
     * 文字内容
     */
    title?: ReactNode;
    /**
     * link是否是A标签
     */
    isALabel?: boolean;
};

export interface AnchorProps extends BasePropsWithChildren {
    /**
     * 尺寸
     */
    size?: 'small' | 'medium';
    /**
     * inline: 不带侧边
     */
    type?: 'inline' | 'normal';
    /**
     * 触发偏移量，距离窗口（容器）顶部
     */
    offsetTop?: number;
    /**
     * 是否启动固钉模式
     */
    affix?: boolean;
    /**
     * 锚点区域边界
     */
    bounds?: number;
    /**
     * 指定滚动的容器
     */
    getContainer?: () => HTMLElement | Window;
    /**
     * Link点击
     */
    onClick?(e: MouseEvent, link: {title: ReactNode, href: string}): void;
}
