import {BaseProps} from "../interface";

export interface BackTopProps extends BaseProps {
    /**
     * 是否展示
     */
    visible?: boolean;

    /**
     * 用户点击之后回调函数
     */
    onClick?: () => void;

    /**
     * 用户监听其滚动的元素
     */
    target?: () => HTMLElement | Window;

    /**
     * 滚动高度到达此致显示BackTop按钮
     */
    visibilityHeight?: number;
};
