import {ReactNode, MouseEvent} from 'react';
import {BasePropsWithChildren} from '../interface';

export interface BreadcrumbProps extends BasePropsWithChildren {
    /**
     * 分割符
     */
    separator?: ReactNode;
    /**
     * 尺寸
     */
    size?: 'medium' | 'small';
    /**
     * 类型
     */
    type?: 'normal' | 'strong';
};

export interface BreadcrumbItemProps extends BasePropsWithChildren {
    /**
     * 分隔符
     */
    separator?: ReactNode;
    /**
     * 跳转链接, 作用于a标签上
     */
    href?: string;
    /**
     * 点击crumbItem的回调函数
     */
    onClick?(event: MouseEvent): void;
    /**
     * 禁用
     */
    disabled?: boolean;
};