import React, {ReactNode} from 'react';

export interface CheckboxGroupContext {
    toggleOption(option: {
        label: ReactNode;
        value: string | number;
        disabled?: boolean;
        exclusive?: boolean;
    }): void,
    register(option): void,
    unregister(option): void,
    value: (string | number)[];
    disabled: boolean;
    size: 'medium' | 'small'
    type: 'strong' | 'simple'
}

export default React.createContext<CheckboxGroupContext>(null);
