// 组件前缀
export const PREFIX = 'one';

// 组件可见边界，通常用来设置border(原组件设计边界不清，所以增加该标记)
export const COMPONENT_MAIN = PREFIX + '-main';

// 焦点
export const COMPONENT_FOCUS = PREFIX + '-focus';

// 错误
export const COMPONENT_INVALID = PREFIX + '-invalid';

// 主题
export const THEME = PREFIX + '-theme';

// 无边框d22主题
export const THEME_LIGHT_D22 = 'light-d22';
