import Dialog from './dialog';
import confirm from './confirm';
import toast from '../toast';
import {DialogAlertProps, DialogConfirmProps} from './interface';

Dialog.confirm = (props: DialogConfirmProps) => {
    const config: DialogConfirmProps = {
        type: 'confirm',
        okCancel: true,
        okType: 'primary',
        okText: '确定',
        cancelText: '取消',
        cancelType: 'normal',
        size: 'medium',
        ...props
    };
    return confirm(config);
};

Dialog.alert = (props: DialogAlertProps) => {
    const config: DialogConfirmProps = {
        type: 'alert',
        okCancel: false,
        okType: 'primary',
        okText: '确定',
        buttonPosition: 'center',
        size: 'medium',
        ...props
    };
    return confirm(config);
};

Dialog.toast = toast;

export default Dialog;
export * from './interface';
