
import React from 'react';
import PropTypes from 'prop-types';
import {Consumer} from '../providerConfig/context';
import {formatCommonConfig} from '../../core/commonTools';
import Embedded from './embedded';
import Horizon from './horizon';

/**
 * @deprecated
 */
function EmbeddedContainer(props) {
    const {
        suffixCls,
        ...restProps
    } = props;
    return (
        <Consumer>
            {
                consumerConfig => {
                    const {prefixCls, size} = formatCommonConfig(
                        {
                            ...props,
                            suffixCls
                        }, consumerConfig
                    );
                    return <Embedded {...restProps} size={size} prefixCls={prefixCls} />;
                }
            }
        </Consumer>
    );
}

EmbeddedContainer.propTypes = {
    suffixCls: PropTypes.string
};

EmbeddedContainer.defaultProps = {
    suffixCls: 'embedded'
};

/**
 * @deprecated
 */
function HorizonContainer(props) {
    const {
        suffixCls,
        ...restProps
    } = props;
    return (
        <Consumer>
            {
                consumerConfig => {
                    const {prefixCls, size} = formatCommonConfig(
                        {
                            ...props,
                            suffixCls
                        }, consumerConfig
                    );
                    return <Horizon {...restProps} size={size} prefixCls={prefixCls} />;
                }
            }
        </Consumer>
    );
}

HorizonContainer.propTypes = {
    suffixCls: PropTypes.string
};

HorizonContainer.defaultProps = {
    suffixCls: 'embedded-horizon'
};

EmbeddedContainer.Horizon = HorizonContainer;

export default EmbeddedContainer;