import React, {forwardRef} from "react";
import {EmptyProps} from "./interface";
import {classnames} from '../../core/commonTools';
import {IllustrationSpotNoContent} from 'dls-illustrations-react';

function renderPart(part, className) {
    if (part) {
        return (<div className={className}>{part}</div>)
    }
    return null;
}

/**
 * @visibleName Empty
 */
export const Empty = forwardRef<HTMLDivElement, EmptyProps>(
    ({
        className,
        prefixCls,
        size,
        title,
        description,
        actions,
        children,
        ...restProps
    },
    ref
) => (
    <div
        ref={ref}
        className={classnames(`${prefixCls}`, `${prefixCls}-${size}`, className)}
        {...restProps}
    >
        {renderPart(children, `${prefixCls}-image`)}
        {renderPart(title, `${prefixCls}-title`)}
        {renderPart(description, `${prefixCls}-description`)}
        {renderPart(actions, `${prefixCls}-actions`)}
    </div>
));

Empty.defaultProps = {
    prefixCls: 'one-empty',
    description: '暂无数据',
    size: 'medium',
    children: <IllustrationSpotNoContent />
};

Empty.displayName = 'Empty';

export default Empty;
export * from '../interface';
