import React from 'react';
import {classnames} from '../../core/commonTools';
import {ColProps, ColBaseProps} from './interface';

const colSize = ['xs', 'sm', 'md', 'lg', 'xl'];

export const GridCol = (props: ColProps) => {
    const {span, order, offset, push, pull, className, children, prefixCls = 'one-col', ...others} = props;
    let sizeClassObj = {};
    colSize.forEach(size => {
        let sizeProps: ColBaseProps = {};
        if (typeof props[size] === 'number') {
            sizeProps.span = props[size];
        }
        else if (typeof props[size] === 'object') {
            sizeProps = props[size] || {};
        }
        delete others[size];
        sizeClassObj = {
            ...sizeClassObj,
            [`${prefixCls}-${size}-${sizeProps.span}`]: sizeProps.span !== undefined,
            [`${prefixCls}-${size}-order-${sizeProps.order}`]: sizeProps.order || sizeProps.order === 0,
            [`${prefixCls}-${size}-offset-${sizeProps.offset}`]: sizeProps.offset || sizeProps.offset === 0,
            [`${prefixCls}-${size}-push-${sizeProps.push}`]: sizeProps.push || sizeProps.push === 0,
            [`${prefixCls}-${size}-pull-${sizeProps.pull}`]: sizeProps.pull || sizeProps.pull === 0
        };
    });
    const classes = classnames({
        [`${prefixCls}-${span}`]: span !== undefined,
        [`${prefixCls}-order-${order}`]: order,
        [`${prefixCls}-offset-${offset}`]: offset,
        [`${prefixCls}-push-${push}`]: push,
        [`${prefixCls}-pull-${pull}`]: pull
    }, className, sizeClassObj);
    return <div {...others} className={classes}>{children}</div>;
};

export default GridCol;
