import {CSSProperties, ReactNode} from 'react';
import {THEME_LIGHT_D22} from './config';

/**
 * 组件基本属性
 */
export interface BaseProps {
     /**
     * 类名前缀
     */
    prefixCls?: string;
    /**
     * 类名
     */
    className?: string;
    /**
     * 样式
     */
    style?: CSSProperties;
    /**
     * 主题
     */
    theme?: typeof THEME_LIGHT_D22 | string;
};

/**
 * 组件基本属性
 */
export interface BasePropsWithChildren extends BaseProps {
   children?: ReactNode;
};