import React, {PureComponent} from 'react';
import {LayoutContentProps} from './interface';
import {withConfigConsumer} from '../providerConfig/context';
import {classnames} from '../../core/commonTools';

@withConfigConsumer('layout-content')
class LayoutContent extends PureComponent<LayoutContentProps> {
    static defaultProps: LayoutContentProps = {
        prefixCls: 'one-layout-content'
    }
    render() {
        const {
            className,
            prefixCls,
            children,
            ...restProps
        } = this.props;
        return (
            <main
                className={classnames(prefixCls, className)}
                {...restProps}
            >
                {children}
            </main>
        );
    }
}

export default LayoutContent;
