import React, {PureComponent} from 'react';
import {LayoutHeaderProps} from './interface';
import {withConfigConsumer} from '../providerConfig/context';
import {classnames} from '../../core/commonTools';

@withConfigConsumer('layout-header')
class LayoutHeader extends PureComponent<LayoutHeaderProps> {
    static defaultProps: LayoutHeaderProps = {
        prefixCls: 'one-layout-header',
        sticky: false
    }

    render() {
        const {
            className,
            prefixCls,
            children,
            sticky,
            ...restProps
        } = this.props;
        return (
            <header
                className={
                    classnames(prefixCls, className, {
                        [`${prefixCls}-sticky`]: sticky
                    })
                }
                {...restProps}
            >
                {children}
            </header>
        );
    }
}

export default LayoutHeader;
