
import React, {ReactNode, PureComponent, CSSProperties} from 'react';
import {classnames} from '../../core/commonTools';

interface OverlayContentProps {
    prefixCls: string;
    className: string;
    overlay: ReactNode;
    id: string;
    trigger;
    style: CSSProperties;
};

export default class Content extends PureComponent<OverlayContentProps> {

    componentDidUpdate() {
        const {trigger} = this.props;
        if (trigger) {
            trigger.forcePopupAlign();
        }
    }

    render() {
        const {
            overlay,
            prefixCls,
            id,
            className,
            style
        } = this.props;
        return (
            <div
                className={classnames(`${prefixCls}-inner`, className)}
                id={id}
                style={style}
            >
                {typeof overlay === 'function' ? overlay() : overlay}
            </div>
        );
    }
}