import React, {PureComponent} from 'react';
import Radio from './radio';
import Context from './context';
import {RadioButtonProps} from './interface';

class RadioButton extends PureComponent<RadioButtonProps> {

    static defaultProps = {
        prefixCls: 'one-radio-button',
        size: 'medium'
    };

    render() {
        return (
            <Context.Consumer>
                {context => this.renderRadio(context)}
            </Context.Consumer>
        );
    }

    renderRadio(context) {
        const {
            type,
            ...props
        } = this.props;

        return (
            <Radio {...props} type={type || context?.type || 'strong'} />
        );
    }
}

export default RadioButton;
