import React, {PureComponent} from 'react';
import {classnames} from '../../core/commonTools';
import {withConfigConsumer} from '../providerConfig/context';
import {SortableHandleProps} from './interface';

@withConfigConsumer('sortable-handle')
class SortableHandle extends PureComponent<SortableHandleProps> {

    static defaultProps = {
        prefixCls: 'one-sortable-handle',
        tag: 'span'
    };

    render() {
        const {
            tag: Tag,
            prefixCls,
            className,
            children,
            ...restProps
        } = this.props;

        return (
            <Tag
                className={classnames(prefixCls, className)}
                {...restProps}
            >
                {children}
            </Tag>
        );
    }
}

export default SortableHandle;
