import {BasePropsWithChildren} from '../interface';

export type StackAlign = 'start' | 'end' | 'center' | 'stretch';

export type StackDirection = 'row' | 'column';

export type StackJustify = 'start' | 'end' | 'center' | 'space-between';

export type StackGap = 'xxsmall' | 'xsmall' | 'small' | 'medium' | 'large' | 'xlarge' | 'xxlarge' | number;

export interface StackProps extends BasePropsWithChildren {

    /**
     * 对齐方式 当 direction 为 row 时，默认为 center；当 direction 为 column 时，默认为 stretch
     */
    align?: StackAlign;

    /**
     * 排列方向
     */
    direction?: StackDirection;

    /**
     * 分布方式
     */
    justify?: StackJustify;

    /**
     * 间距 可以设置为字符串或数字，字符串时可选值为 xxsmall / xsmall / small / medium / large / xlarge / xxlarge，数字时单位为 px。
     */
    gap?: StackGap;

    /**
     * 是否换行
     */
    wrap?: boolean;

    /**
     * 是否在行内渲染
     */
    inline?: boolean;
};
