import React, {PureComponent} from 'react';
import {classnames} from '../../../core/commonTools';

interface DragLineProps {
    prefixCls: string;
}

class DragLine extends PureComponent<DragLineProps> {

    state = {
        dragLeft: 0,
        showLine: false
    };

    setShowLine = showLine => {
        this.setState({
            showLine
        });
    }

    setDragLeft = dragLeft => {
        this.setState({
            dragLeft
        });
    }

    render() {
        const prefixCls = this.props.prefixCls;
        const {dragLeft, showLine} = this.state;
        return (
            <div
                key="drag-line"
                style={{
                    left: dragLeft
                }}
                className={classnames(`${prefixCls}-drag-line`, {
                    [`${prefixCls}-drag-line-show`]: showLine
                })}
            />
        );
    }
}

export default DragLine;
