import React, {PureComponent, ReactNode} from 'react';
import {
    getActiveIndex
} from '../../../core/tabsTools';


interface ScrollContainerProps {
    title: ReactNode[],
    content: ReactNode[],
    prefixCls: string,
    activeKey: string
}

export default class ScrollContainer extends PureComponent<ScrollContainerProps> {

    render() {
        const {content, prefixCls, title, activeKey} = this.props;
        const activeIndex = getActiveIndex(title, activeKey);
        const style = activeIndex ? {
            marginLeft: `-${activeIndex * 100}%`
        } : {};
        return (
            <div className={`${prefixCls}-content`} style={style}>
                {content}
            </div>
        );
    }
}
