import React from 'react';
import {
    IconCheckCircle,
    IconExclamationCircle,
    IconInfoCircle
} from 'dls-icons-react';

const statusIcons = {
    success: IconCheckCircle,
    info: IconInfoCircle,
    error: IconInfoCircle,
    warning: IconExclamationCircle
};

export default function StatusIcon({status, prefixCls}) {
    const Icon = statusIcons[status];
    const className = `${prefixCls}-tab-item-status ${prefixCls}-tab-item-status-${status}`;
    return Icon
        ? <span className={`${prefixCls}-tab-item-status-container`}><Icon className={className} /></span>
        : null;
}
