import React, {ReactNode} from 'react';

type ContentProps = {
    prefixCls: string,
    overlay: ReactNode
};

const Content = (props: ContentProps) => {
    const {overlay, prefixCls} = props;
    return (
        <div className={`${prefixCls}-inner`} role="tooltip">
            {typeof overlay === 'function' ? overlay() : overlay}
        </div>
    );
};

export default Content;
