import React, {ReactNode} from 'react';
import Button, {ButtonProps} from '../../button';

interface CommonFooterProps {
    treeName: string;
    candidateFooterRender: ReactNode | ((props: CommonFooterProps) => ReactNode);
    candidateFooterProps: ButtonProps;
    size: 'small' | 'medium';
    prefixCls: string;
};

const CommonFooterRender = (props: CommonFooterProps) => {
    const {
        candidateFooterProps,
        treeName,
        size,
        prefixCls,
        candidateFooterRender
    } = props;
    if (typeof candidateFooterRender === 'function') {
        return candidateFooterRender(props);
    }
    else if (typeof candidateFooterRender === 'object') {
        return candidateFooterRender;
    }
    return (
        <div className={`${prefixCls}-select-footer`}>
            <Button type="text-strong" size={size} {...candidateFooterProps}>
                + 新建
                {treeName}
            </Button>
        </div>
    );
};

CommonFooterRender.defaultProps = {
    treeName: '',
    candidateFooterProps: {},
    size: 'medium'
};

export default CommonFooterRender;
