import React from 'react';
import {
    IconImageAdd,
    IconVideoAdd,
    IconMediaAdd,
    IconImageSolid,
    IconVideoSolid,
    IconVideoFilmSolid
} from 'dls-icons-react';
import {THEME_LIGHT_D22} from '../../config';

const icons = {
    image: IconImageAdd,
    video: IconVideoAdd,
    media: IconMediaAdd
};

const d22Icons = {
    image: IconImageSolid,
    video: IconVideoSolid,
    media: IconVideoFilmSolid
};

export default ({theme, type, className}) => {
    const Icon = (theme === THEME_LIGHT_D22 ? d22Icons : icons)[type];
    if (!Icon) {
        return null;
    }
    return <Icon className={className} />;
};
