export const convertToCamelCase = str => {
    if (!str) {
        return;
    }
    const strArr = str.split('-');
    if (strArr[0] === '') {
        strArr.shift();
    }
    for (let i = 1, len = strArr.length; i < len; i++) {
        // 如果不为空，则转成大写
        if (strArr[i] !== '') {
            strArr[i] = strArr[i][0].toUpperCase() + strArr[i].substring(1);
        }
    }
    return `Icon${strArr.join('')}`;
};
