import warning from 'warning';

export const propChangeWarning = (
    component: string,
    props: Record<string, any>,
    keyMap: Record<string, string>
) => {
    Object.keys(keyMap).forEach(key => {
        warning(
            !(key in props),
            `The \`${key}\` prop of \`${component}\` is deprecated, use \`${keyMap[key]}\` instead'`
        );
    });
};
