import { BasePropsWithChildren } from '../interface';
export interface AffixProps extends BasePropsWithChildren {
    /**
     * 距离容器顶部达到指定偏移量后固定
     */
    offsetTop?: number;
    /**
     * 距离容器底部到达指定偏移量后固定
     */
    offsetBottom?: number;
    /**
     *  固定状态改变时回调函数
     */
    onChange?: (affixed: boolean) => void;
    /**
     * 需要监听其滚动事件的元素，值为一个返回对应 DOM 元素的函数
     */
    target?: () => HTMLElement | Window;
    /**
     * 固钉的z-index
     */
    zIndex?: number;
}
