import { ReactNode } from 'react';
import { BasePropsWithChildren } from '../interface';
export interface BadgeProps extends BasePropsWithChildren {
    /**
     * count 展示的数字
     */
    count?: number;
    /**
     * 自定义圆点的颜色
     */
    color?: string;
    /**
     * 不展示数字，只展示小圆点
     */
    isDot?: boolean;
    /**
     * 设置状态点的位置偏移
     */
    offset?: [number, number];
    /**
     * 设置封顶的数字，比如overflowCount为99，当count > 99的时候，显示 99+
     */
    overflowCount?: number;
    /**
     * 当count为0的时候，是否还要展示badge
     */
    showZero?: boolean;
    /**
     * 小圆点的状态
     */
    type?: 'success' | 'warning' | 'error' | 'processing' | 'default';
    /**
     * 设置了type情况下，小圆点旁边展示的文字
     */
    text?: string;
    /**
     * 是否展示badge
     */
    visible?: boolean;
    /**
     * 自定义内容
     */
    textContent?: ReactNode;
}
