import { ReactNode } from 'react';
import { BasePropsWithChildren } from '../interface';
export interface CardProps extends BasePropsWithChildren {
    /**
     * 尺寸
     */
    size?: 'small' | 'medium' | 'large' | 'xlarge';
    /**
     * 是否有边框
     */
    bordered?: boolean;
    /**
     * 圆角
     */
    borderRadius?: 'small' | 'medium' | 'large' | 'xlarge' | number;
    /**
     * 标题
     */
    header?: ReactNode;
    /**
     * 标题区与内容区分割线
     */
    headerLine?: boolean;
    /**
     * 右上角自定义区
     */
    headerExtra?: ReactNode;
    /**
     * 底部
     */
    footer?: ReactNode;
    /**
     * 底部区与内容区分割线
     */
    footerLine?: boolean;
    /**
     * 阴影
     */
    shadow?: 'hover' | 'aways';
}
