import { CSSProperties, ReactNode } from 'react';
import { BasePropsWithChildren } from '../interface';
export interface CarsouselProps extends BasePropsWithChildren {
    /**
     * 是否自动播放
     */
    autoplay?: boolean;
    /**
     * 自动播放时间
     */
    autoplaySpeed?: number;
    /**
     * 每次滚动几张
     */
    slidesToScroll?: number;
    /**
     * 宽度
     */
    width?: number | string;
    /**
     * 每屏幕展示多少张
     */
    slidesToShow?: number;
    /**
     * 是否无限循环
     */
    infinite?: boolean;
    /**
     * 切换前的function
     */
    beforeChange?(current: number): void;
    /**
     * 切换后的function
     */
    afterChange?(current: number): void;
    /**
     * 模式， 多片滚动模式，单片模式，默认多片滚动
     */
    mode?: 'multiple' | 'single';
    /**
     * dotPosition, 面板指示点的位置，如果dotPosition为left, right，表示垂直滚动
     * dotPostion为bottom和top的时候为左右滚动
     */
    dotPosition?: 'top' | 'bottom' | 'left' | 'right';
    /**
     * sliderMode - 切换的模式，支持两种 滑动条 or 按钮，后续支持点、数字
     */
    sliderMode?: 'line' | 'number' | 'dot' | 'hide' | 'button';
    /**
     * 自定义后翻页
     */
    nextArrow?: ReactNode;
    /**
     * 自定义前翻页
     */
    prevArrow?: ReactNode;
    /**
     * 是否展示翻页
     */
    showButton?: boolean;
    /**
     * 前翻页按钮属性
     */
    prevButtonProps?: {
        className?: string;
        style?: CSSProperties;
    };
    /**
     * 后翻页按钮属性
     */
    nextButtonProps?: {
        className?: string;
        style?: CSSProperties;
    };
    /**
     * 自定义页码区
     */
    customSuffix?: ReactNode;
    /**
     * 初始页
     */
    initialSlide?: number;
}
