// 组件前缀
export var PREFIX = 'one'; // 组件可见边界，通常用来设置border(原组件设计边界不清，所以增加该标记)

export var COMPONENT_MAIN = function COMPONENT_MAIN(prefix) {
  return (prefix || PREFIX) + '-main';
}; // 焦点

export var COMPONENT_FOCUS = function COMPONENT_FOCUS(prefix) {
  return (prefix || PREFIX) + '-focus';
}; // 错误

export var COMPONENT_INVALID = function COMPONENT_INVALID(prefix) {
  return (prefix || PREFIX) + '-invalid';
}; // 主题

export var THEME = function THEME(prefix) {
  return (prefix || PREFIX) + '-theme';
}; // 默认主题

export var THEME_LIGHT_D20 = 'light-d20'; // 无边框d22主题

export var THEME_LIGHT_D22 = 'light-d22'; // AI主题

export var THEME_LIGHT_AI = 'light-ai'; // AI主题样式前缀(不直接用light-ai是因为可能会和AI组件重名)

export var THEME_LIGHT_AI_PREFIX = 'one-ai';