import { ReactElement } from 'react';
import { BasePropsWithChildren } from '../interface';
export interface MessageProps extends BasePropsWithChildren {
    /**
     * 尺寸
     */
    size?: 'small' | 'medium';
    /**
     * 类型：信息 | 错误 | 成功 | 警告 | 辅助信息
     */
    type?: 'info' | 'error' | 'success' | 'warning' | 'aux';
    /**
     * 自定义Icon
     */
    icon?: ReactElement;
    /**
     * 展示方式
     */
    display?: 'normal' | 'popup' | 'simple' | 'standalone';
}
