import { ReactNode } from 'react';
import { BaseProps } from '../interface';
export interface RatingProps extends Omit<React.HTMLAttributes<HTMLSpanElement>, 'onChange'>, BaseProps {
    /**
     * 默认值
     */
    defaultValue?: number;
    /**
     * 当前值，受控
     */
    value?: number;
    /**
     * 变化回调
     */
    onChange?: (value: number) => void;
    /**
     * 只读
     */
    readOnly?: boolean;
    /**
     * 半选
     */
    allowHalf?: boolean;
    /**
     * 允许清空
     */
    allowClear?: boolean;
    /**
     * 标签展示
     */
    labels?: Record<number, ReactNode>;
    /**
     * 标签展示方式
     */
    labelDisplay?: 'inline' | 'popup';
    /**
     * 个数
     */
    max?: number;
    /**
     * 自定义
     */
    symbol?: ReactNode | ((value: number, selectedValue: number, hoveredValue: number) => ReactNode);
}
