import { MouseEvent } from "react";
import { BaseProps } from "../interface";
export interface SwitchProps extends BaseProps {
    /**
     * 禁用
     */
    disabled?: boolean;
    /**
     * 当前是否被选中
     */
    checked?: boolean;
    /**
     * 默认是否被选中
     */
    defaultChecked?: boolean;
    /**
     * 尺寸
     */
    size?: 'xsmall' | 'small' | 'medium';
    /**
     * 是否loading
     */
    loading?: boolean;
    /**
     * 变化时回调
     */
    onChange?(checked: boolean, e: MouseEvent): void;
}
